mapper.update:
UPDATE t_class
SET class_name = #{classDto.className},
department_id = (SELECT id FROM t_department WHERE department_name = #{classDto.departmentName}),
school_id = (SELECT school_id FROM t_department WHERE department_name = #{classDto.departmentName}
             AND school_id = (SELECT id FROM t_school WHERE school_name = #{classDto.schoolName}))
WHERE id = #{classDto.id}
